      background-color: @surface0;
    }
    .thread-header__title-bar-container--focused {
      background-color: @mantle;
    }
    .thread-header__title-bar-container {
      background: @mantle;
    }
    .thread-header__click-area:focus .thread-header__title-bar-container {
      background-color: @mantle;
    }
    .navigation-link {
      color: @text;
      &:hover {
