      color: @text;
    }

    .cOJBQO {
      color: @text;
    }

    .dBlttx {
      color: @subtext1;
    }

    .fiCviy {
      color: @text;
    }

    .server-message-alert {
      border-color: @red !important;
      border-left-color: @red !important;
    }

